INSERT INTO `pms_product` VALUES ('108', '5', '92', '17', '东湖菊花礼盒装', 'https://haojiang-1332489043.cos.ap-guangzhou.myqcloud.com/mall/20241226/20241222220.jpg', 'JH01', '0', '1', '1', '1', '0', '0', '0', '148.00', null, '0', '0', '0', '疏散风热，平抑肝阳，清肝明目，清热解毒', '1.疏散风热，\n菊花性微寒，味苦辛，能够发散表邪，清凉解表，适用于治疗风热感冒、温病初起等症状，如发热、微恶风寒、头痛、咳嗽等。\n2.平抑肝阳\n菊花入肝经，能清肝热、平肝阳，适用于治疗肝阳上亢引起的头痛眩晕、目赤肿痛等症状。\n3.清肝明目\n菊花苦泄微寒，入肝经，既能疏散肝经风热，又能清泄肝热以明目，适用于治疗肝经风热或肝火上攻所致的目赤肿痛、眼目昏花等症状。\n4.清热解毒\n菊花性寒，可用于治疗疮痈肿毒、毛囊炎、疖肿等皮肤炎症性疾病，同时也有助于预防中暑。\n使用注意：\n菊花性寒，脾胃虚寒、腹痛、便稀的患者不宜饮用。\n在使用菊花进行自我调理前，建议咨询专业医师，以确保安全有效。', '148.00', '100', '0', '4罐', '200.00', '0', '1,2,3', '', '', 'https://haojiang-1332489043.cos.ap-guangzhou.myqcloud.com/mall/20241226/20241222224.jpg,https://haojiang-1332489043.cos.ap-guangzhou.myqcloud.com/mall/20241226/20241222225.jpg,https://haojiang-1332489043.cos.ap-guangzhou.myqcloud.com/mall/20241226/20241222226.jpg,https://haojiang-1332489043.cos.ap-guangzhou.myqcloud.com/mall/20241226/图片1.png', '', '', '<p><img class=\"wscnph\" src=\"https://haojiang-1332489043.cos.ap-guangzhou.myqcloud.com/mall/images/9b32da8b7e8349fdaae5861ad6d924e8.jpg\" /></p>', '<p><img class=\"wscnph\" src=\"https://haojiang-1332489043.cos.ap-guangzhou.myqcloud.com/mall/images/b6c19163dc2946089683c6df54752772.png\" /></p>', null, null, '0', '0', '东湖菊花', '茶咖', '0', '0', '0.00', null, '0', null, '0', null);
