-- ----------------------------
-- View structure for v_selfcheck_stats
-- ----------------------------
DROP VIEW IF EXISTS `v_selfcheck_stats`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `v_selfcheck_stats` AS select 'guest' AS `user_type`,count(0) AS `total_count`,count((case when (`ums_guest`.`status` = 1) then 1 end)) AS `active_count`,count((case when (`ums_guest`.`has_active_order` = 1) then 1 end)) AS `with_order_count`,count((case when (`ums_guest`.`last_active_time` > (now() - interval 1 hour)) then 1 end)) AS `recent_active_count` from `ums_guest` union all select 'member' AS `user_type`,count(0) AS `total_count`,count((case when (`ums_member_session`.`status` = 1) then 1 end)) AS `active_count`,count((case when (`ums_member_session`.`order_in_progress` = 1) then 1 end)) AS `with_order_count`,count((case when (`ums_member_session`.`last_active_time` > (now() - interval 1 hour)) then 1 end)) AS `recent_active_count` from `ums_member_session` ;
