mapper.update:
UPDATE pms_sku_stock SET stock = CASE id
WHEN #{item.productSkuId} THEN stock - #{item.productQuantity}
END, lock_stock = CASE id
WHEN #{item.productSkuId} THEN lock_stock - #{item.productQuantity}
END WHERE id IN
#{item.productSkuId}

UPDATE pms_store_sku_stock
SET
    stock = CASE 

      WHEN store_id = #{storeId} AND sku_id = #{item.productSkuId} THEN stock - #{item.productQuantity}

    END,
    locked_stock = CASE 

      WHEN store_id = #{storeId} AND sku_id = #{item.productSkuId} THEN locked_stock - #{item.productQuantity}

    END,
    sale_count = CASE 

      WHEN store_id = #{storeId} AND sku_id = #{item.productSkuId} THEN sale_count + #{item.productQuantity}

    END
WHERE
    store_id = #{storeId}
    AND sku_id IN

    #{item.productSkuId}


UPDATE pms_sku_stock
SET
    stock = CASE id

      WHEN #{item.productSkuId} THEN stock - #{item.productQuantity}

    END,
    lock_stock = CASE id

      WHEN #{item.productSkuId} THEN lock_stock - #{item.productQuantity}

    END,
    sale = CASE id

      WHEN #{item.productSkuId} THEN sale + #{item.productQuantity}

    END
WHERE
    id IN

    #{item.productSkuId}
;


UPDATE pms_store_sku_stock
SET
    stock = CASE 

      WHEN store_id = #{storeId} AND sku_id = #{item.productSkuId} THEN stock - #{item.productQuantity}

    END,
    locked_stock = CASE 

      WHEN store_id = #{storeId} AND sku_id = #{item.productSkuId} THEN locked_stock - #{item.productQuantity}

    END,
    sale_count = CASE 

      WHEN store_id = #{storeId} AND sku_id = #{item.productSkuId} THEN sale_count + #{item.productQuantity}

    END
WHERE
    store_id = #{storeId}
    AND sku_id IN

    #{item.productSkuId}

UPDATE pms_product SET sale = CASE id
WHEN #{item.productId} THEN sale + #{item.productQuantity}
END WHERE id IN
#{item.productId}

update oms_order
set status=#{status}
where id in

    #{id}

UPDATE pms_sku_stock SET lock_stock = CASE id
WHEN #{item.productSkuId} THEN lock_stock - #{item.productQuantity}
END WHERE id IN
#{item.productSkuId}

UPDATE pms_store_sku_stock
SET
locked_stock = CASE 

    WHEN store_id = #{storeId} AND sku_id = #{item.productSkuId} THEN locked_stock - #{item.productQuantity}

END
WHERE
store_id = #{storeId}
AND sku_id IN

    #{item.productSkuId}
